import React, { useEffect, useState, useRef  } from 'react';
import Navbar from '../Navbar';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import axios from 'axios';
import $ from 'jquery';
import Swal from 'sweetalert2';

function AddTrain() {
    const auth_userid = localStorage.getItem("id");
    const [showModal, setShowModal] = useState(false);
    const [showDeleteModal, setShowDeleteModal] = useState(false);
    const [AllTrain, setAllTrain] = useState([]); 
    const [editingAllTrain, seteditingAllTrain] = useState(null);
    const [deletingAllTrain, setDeletingAllTrain] = useState(null);
    const [formData, setFormData] = useState({
        User_Id: auth_userid,
        Train_Name: '',
        Train_Description: '',
        Train_Location: '',
        Train_DateTime: '',
        Ticket_Price: '',
        Ticket_Count: '',
        Commission_Rate: '',
      });
      const [formErrors, setFormErrors] = useState({
        User_Id: auth_userid,
        Train_Name: '',
        Train_Description: '',
        Train_Location: '',
        Train_DateTime: '',
        Ticket_Price: '',
        Ticket_Count: '',
        Commission_Rate: '',
      });

      const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => {
    setShowModal(false);
    setFormData({
        User_Id: auth_userid,
        Train_Name: '',
        Train_Description: '',
        Train_Location: '',
        Train_DateTime: '',
        Ticket_Price: '',
        Ticket_Count: '',
        Commission_Rate: '',
    });
    setFormErrors({
        User_Id: auth_userid,
        Train_Name: '',
        Train_Description: '',
        Train_Location: '',
        Train_DateTime: '',
        Ticket_Price: '',
        Ticket_Count: '',
        Commission_Rate: '',
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const tableRef = useRef(null);
  const fetchData = async () => {
    try {
      const response = await axios.get('https://localhost:7207/api/Train/Train');
      setAllTrain(response.data);

      if ($.fn.DataTable.isDataTable('#tableId')) {
        tableRef.current.DataTable().destroy();
      }
      console.log(response.data);
      tableRef.current = $('#tableId').DataTable({
        data: response.data,
        columns: [
          { data: 'Train_Name', title: 'Train Name' },
          { data: 'Train_DateTime', title: 'Train Date Time' },
          { data: 'ticket_Price', title: 'Ticket Price' },
          { data: 'ticket_Count', title: 'Ticket Count' },
          { data: 'commission_Rate', title: 'Commission Rate' },
          { data: 'status_Type', title: 'Status' },
          {
            data: null,
            render: renderActionButtons,
          },
        ],
        language: {
          emptyTable: 'No data available in table',
        },
      });
    } catch (error) {
      console.error('Error fetching All Train list', error);
    }
  };

  const renderActionButtons = (data, type, row) => {
    return (
      '<center>' +
      '<button type="button" class="btn btn-success btn-sm" onclick="window.handleEdit(' +
      row.Train_id  + ', \'' + row.Train_Name + '\', \'' + row.Train_Description +'\', \'' + row.Train_Location +'\', \'' + row.Train_DateTime +'\', \'' + row.ticket_Price +'\', \'' + row.ticket_Count +'\', \'' + row.commission_Rate +'\')"><i class="bi bi-pencil-square"></i> Edit</button>' +
      '&nbsp;' +
      '<button type="button" class="btn btn-danger btn-sm" onclick="window.handleDelete(' +
      row.Train_id +
      ')">Delete</button>' +
      '</center>'
    );
  };


  window.handleEdit = (Train_id, Train_Name, Train_Description, Train_Location, Train_DateTime, Ticket_Price, Ticket_Count, Commission_Rate) => {
    seteditingAllTrain(Train_id);
    setFormData({
        Train_id: Train_id,
        Train_Name: Train_Name,
        Train_Description: Train_Description,
        Train_Location: Train_Location,
        Train_DateTime: Train_DateTime,
        Ticket_Price: Ticket_Price,
        Ticket_Count: Ticket_Count,
        Commission_Rate: Commission_Rate,
    });
    handleShowModal();
};


  window.handleDelete = (Train_id) => {
    setDeletingAllTrain(Train_id);
    handleShowDeleteModal();
  };

  const handleDeleteAllTrain = async () => {
    try {
    await axios.delete(`https://localhost:7207/api/Train/Train/${deletingAllTrain}`);
    console.log('Train deleted successfully');
    Swal.fire({title: 'Success', text: '', icon: 'success' }).then((result) => {
      if (result.isConfirmed) {
        window.location.reload();
        window.scrollTo({top: 0,behavior: 'smooth'});
      }
    });
    handleCloseDeleteModal();
    
    } catch (error) {
    console.error('Error deleting Train', error);
    Swal.fire({title: 'Warning', text: 'Something went wrong..!', icon: 'error' }).then((result) => {
      if (result.isConfirmed) {
        window.scrollTo({top: 0,behavior: 'smooth'});
      }
    });
    }
};

  const handleShowDeleteModal = () => setShowDeleteModal(true);
  const handleCloseDeleteModal = () => setShowDeleteModal(false);


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };


  const handleAddTrain = async () => {
    try {
      const errors = {};

      if (!formData.Train_Name) {
        errors.Train_Name = 'Train name is required.';
      }
      if (!formData.Train_Description) {
        errors.Train_Description = 'Train description is required.';
      }
      if (!formData.Train_Location) {
        errors.Train_Location = 'Train location is required.';
      }
      if (!formData.Train_DateTime) {
        errors.Permission = 'Train datetime is required.';
      }
      if (!formData.Ticket_Price) {
        errors.Password = 'Ticket price is required.';
      }
      if (!formData.Ticket_Count) {
        errors.Password = 'Ticket count is required.';
      }
      if (!formData.Commission_Rate) {
        errors.Password = 'Commission rate is required.';
      }
    
   
      if (Object.keys(errors).length > 0) {
        setFormErrors(errors);
        return;
      }

    if (editingAllTrain) {
        await axios.put(`https://localhost:7207/api/Train/Train/${editingAllTrain}`, formData);
      } else {
        await axios.post('https://localhost:7207/api/Train/AddTrain', formData);
      }
        console.log('Train added successfully');
        Swal.fire({title: 'Success', text: '', icon: 'success' }).then((result) => {
          if (result.isConfirmed) {
            window.location.reload();
            window.scrollTo({top: 0,behavior: 'smooth'});
          }
        });
        handleCloseModal();
        
    } catch (error) {
      console.error('Train added failed', error);
      Swal.fire({title: 'Warning', text: 'Something went wrong..!', icon: 'error' }).then((result) => {
        if (result.isConfirmed) {
          window.scrollTo({top: 0,behavior: 'smooth'});
        }
      });
    }
  };

  return (
    
    <div>
        <Navbar />

        <div className="container px-4">
        <div className="card mt-4">
          <div className="card-header d-flex justify-content-between align-items-center small">
            <h4>Train List</h4>
            <Button variant="danger" size="sm" onClick={handleShowModal}>
              Add New Train
            </Button>
          </div>
          <div className="card-body">
          <table id="tableId" className="table table-bordered table-striped">
              <thead>
                <tr>
                  <th>Train Name</th>
                  <th>Train Date Time</th>
                  <th>Ticket Price</th>
                  <th>Ticket Count</th>
                  <th>Commission Rate</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody></tbody>
            </table>
          </div>
        </div>
      </div>


      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{editingAllTrain ? 'Edit Train' : 'Add New Train'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        {!editingAllTrain ?
        <form>
            <input type="hidden" className={`form-control ${formErrors.User_Id ? 'is-invalid' : ''}`} id="User_Id" name="User_Id" placeholder="" value={auth_userid} onChange={handleInputChange}/>
            <div className="mb-3">
              <label htmlFor="Train_Name" className="form-label">
                Train Name
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Name ? 'is-invalid' : ''}`} id="Train_Name" name="Train_Name" placeholder="" value={formData.Train_Name} onChange={handleInputChange}/>
              {formErrors.Train_Name && <div className="invalid-feedback">{formErrors.Train_Name}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_Description" className="form-label">
                Train Description
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Description ? 'is-invalid' : ''}`} id="Train_Description" name="Train_Description" placeholder="" value={formData.Train_Description} onChange={handleInputChange}/>
              {formErrors.Train_Description && <div className="invalid-feedback">{formErrors.Train_Description}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_Location" className="form-label">
                Train Location
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Location ? 'is-invalid' : ''}`} id="Train_Location" name="Train_Location" placeholder="" value={formData.Train_Location} onChange={handleInputChange}/>
              {formErrors.Train_Location && <div className="invalid-feedback">{formErrors.Train_Location}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_DateTime" className="form-label">
                Train DateTime
              </label>
              <input type="datetime-local" className={`form-control ${formErrors.Train_DateTime ? 'is-invalid' : ''}`} id="Train_DateTime" name="Train_DateTime" placeholder="" value={formData.Train_DateTime} onChange={handleInputChange}/>
              {formErrors.Train_DateTime && <div className="invalid-feedback">{formErrors.Train_DateTime}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Ticket_Price" className="form-label">
                Ticket Price
              </label>
              <input type="number" className={`form-control ${formErrors.Ticket_Price ? 'is-invalid' : ''}`} id="Ticket_Price" name="Ticket_Price" placeholder="" value={formData.Ticket_Price} onChange={handleInputChange}/>
              {formErrors.Ticket_Price && <div className="invalid-feedback">{formErrors.Ticket_Price}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Ticket_Count" className="form-label">
                Ticket Count
              </label>
              <input type="number" className={`form-control ${formErrors.Ticket_Count ? 'is-invalid' : ''}`} id="Ticket_Count" name="Ticket_Count" placeholder="" value={formData.Ticket_Count} onChange={handleInputChange}/>
              {formErrors.Ticket_Count && <div className="invalid-feedback">{formErrors.Ticket_Count}</div>}
            </div>


            <div className="mb-3">
              <label htmlFor="Commission_Rate" className="form-label">
                Commission Rate
              </label>
              <input type="number" className={`form-control ${formErrors.Commission_Rate ? 'is-invalid' : ''}`} id="Commission_Rate" name="Commission_Rate" placeholder="" value={formData.Commission_Rate} onChange={handleInputChange}/>
              {formErrors.Commission_Rate && <div className="invalid-feedback">{formErrors.Commission_Rate}</div>}
            </div>

  
          </form>
          : 
          
          <form>
            <div className="mb-3">
              <label htmlFor="Train_Name" className="form-label">
                Train Name
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Name ? 'is-invalid' : ''}`} id="Train_Name" name="Train_Name" placeholder="" value={formData.Train_Name} onChange={handleInputChange}/>
              {formErrors.Train_Name && <div className="invalid-feedback">{formErrors.Train_Name}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_Description" className="form-label">
                Train Description
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Description ? 'is-invalid' : ''}`} id="Train_Description" name="Train_Description" placeholder="" value={formData.Train_Description} onChange={handleInputChange}/>
              {formErrors.Train_Description && <div className="invalid-feedback">{formErrors.Train_Description}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_Location" className="form-label">
                Train Location
              </label>
              <input type="text" className={`form-control ${formErrors.Train_Location ? 'is-invalid' : ''}`} id="Train_Location" name="Train_Location" placeholder="" value={formData.Train_Location} onChange={handleInputChange}/>
              {formErrors.Train_Location && <div className="invalid-feedback">{formErrors.Train_Location}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Train_DateTime" className="form-label">
                Train DateTime
              </label>
              <input type="datetime-local" className={`form-control ${formErrors.Train_DateTime ? 'is-invalid' : ''}`} id="Train_DateTime" name="Train_DateTime" placeholder="" value={formData.Train_DateTime} onChange={handleInputChange}/>
              {formErrors.Train_DateTime && <div className="invalid-feedback">{formErrors.Train_DateTime}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Ticket_Price" className="form-label">
                Ticket Price
              </label>
              <input type="number" className={`form-control ${formErrors.Ticket_Price ? 'is-invalid' : ''}`} id="Ticket_Price" name="Ticket_Price" placeholder="" value={formData.Ticket_Price} onChange={handleInputChange}/>
              {formErrors.Ticket_Price && <div className="invalid-feedback">{formErrors.Ticket_Price}</div>}
            </div>
            <div className="mb-3">
              <label htmlFor="Ticket_Count" className="form-label">
                Ticket Count
              </label>
              <input type="number" className={`form-control ${formErrors.Ticket_Count ? 'is-invalid' : ''}`} id="Ticket_Count" name="Ticket_Count" placeholder="" value={formData.Ticket_Count} onChange={handleInputChange}/>
              {formErrors.Ticket_Count && <div className="invalid-feedback">{formErrors.Ticket_Count}</div>}
            </div>

            <div className="mb-3">
              <label htmlFor="Commission_Rate" className="form-label">
                Commission Rate
              </label>
              <input type="number" className={`form-control ${formErrors.Commission_Rate ? 'is-invalid' : ''}`} id="Commission_Rate" name="Commission_Rate" placeholder="" value={formData.Commission_Rate} onChange={handleInputChange}/>
              {formErrors.Commission_Rate && <div className="invalid-feedback">{formErrors.Commission_Rate}</div>}
            </div>

          </form>
          }
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" size="sm" onClick={handleCloseModal}>
            Close
          </Button>
          <Button variant="primary" size="sm" onClick={handleAddTrain}>
            {editingAllTrain ? 'Save Changes' : 'Add Train'}
          </Button>
        </Modal.Footer>
      </Modal>


      <Modal show={showDeleteModal} onHide={handleCloseDeleteModal}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          Are you sure you want to delete this Train?
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" size="sm" onClick={handleCloseDeleteModal}>
            Cancel
          </Button>
          <Button variant="danger" size="sm" onClick={handleDeleteAllTrain}>
            Delete
          </Button>
        </Modal.Footer>
      </Modal>
        
    </div>
  );
}

export default AddTrain;
